mapper.select:
SELECT EQUIPMENT_MODEL_ID, EQUIPMENT_NAME, MANUFACTURING_COMPANY, MANUFACTURER_NUMBER, CATEGORY_ID, REPAIRER, REPAIRER_NUMBER, EXPOSURE_YES_NO, EQUIPMENT_PHOTO, EQUIPMENT_SPEC FROM EQUIPMENT_MODEL
WHERE EQUIPMENT_NAME LIKE CONCAT('%',#{equipmentName},'%')
ORDER BY EQUIPMENT_MODEL_ID DESC LIMIT #{skip} , #{perSheet}
