mapper.insert:

SELECT LAST_INSERT_ID()

INSERT INTO `ORDER`
(
	ORDER_DATE
	, ORDERER
	, RECIPIENT_PHONE
	, TOTAL_PAYMENT
	, METHOD_OF_PAYMENT
	, ORDER_STATUS
	, ACCUMULATE_POINT
)
VALUES
(
	now()
	, #{orderer}
	, #{recipientPhone}
	, #{totalPayment}
	, #{methodOfPayment}
	, #{orderStatus}
	, #{accumulatePoint}
)

